package com.soak.invest.financial.service.imp;import java.util.List;import com.soak.invest.financial.dao.FinancialStatementDao;import com.soak.invest.financial.dao.imp.FinancialStatementDaoImp;import com.soak.invest.financial.model.BalanceSheet;import com.soak.invest.financial.model.FinancialSummary;import com.soak.invest.financial.service.FinancialStatementService;import com.soak.invest.financial.spider.FinancialSpiderFromSohu;import com.soak.invest.financial.spider.SpiderForFinancialStatement;public class FinancialStatementServiceImp implements FinancialStatementService {    private FinancialStatementDao financialStatementDao  = new FinancialStatementDaoImp();  @Override  public List<FinancialSummary> crawlingFinancialStatement(String stockcode) {    SpiderForFinancialStatement  instance = new SpiderForFinancialStatement();    List<FinancialSummary> financialSummarys = instance.getFinancialSummaryData(stockcode) ;    financialStatementDao.saveAnnotatedBean(financialSummarys);    return financialSummarys;  }    public List<FinancialSummary> crawlingFinancialStatementFromSohu(String shortdate){    FinancialSpiderFromSohu  instance = new FinancialSpiderFromSohu();    List<FinancialSummary> financialSummarys = instance.getFinancialSummaryData(shortdate) ;    financialStatementDao.saveAnnotatedBean(financialSummarys);    return financialSummarys;  }      @Override  public List<BalanceSheet> crawlingBalanceSheet(String stockcode, String year) {    SpiderForFinancialStatement  instance = new SpiderForFinancialStatement();    List<BalanceSheet> balanceSheets = instance.getBalanceSheetData(stockcode,year) ;    financialStatementDao.saveAnnotatedBean(balanceSheets);    return balanceSheets;  }    }